import React from "react";

export default class ContactForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: ""
        }
    }

    onFormSubmit = (e) => {
        e.preventDefault();
        if (this.state.firstname.length === 0 ) {
            alert('Enter a valid name')
        }
    }

    onFirstNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            firstname: e.target.value
        })
    }

    onLastNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            lastname: e.target.value
        })
    }

    onContactNumberInput = (e) => {
        console.log(e.target.value);
        this.setState({
            contact: e.target.value
        })
    }

    render() {
        const display = {
            display: this.props.display
        }
        console.log('firstname is ', this.state.firstname)
        return (
            <div style={display}>
                <form className="form" onSubmit={this.onFormSubmit} >
                    <label>
                        Firstname
                        <input type="text" onChange={this.onFirstNameInput} />
                    </label>
                    <label>
                        lastname
                        <input type="text" onChange={this.onLastNameInput} />
                    </label>
                    <label>
                        Phone Number
                        <input type="tel" onChange={this.onContactNumberInput} />
                    </label>
                    <input type="submit" value="Add contact" />
                </form>
            </div>
        );
    }
}
