import React from "react";

export default class ContactForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: ""
        }
    }


    onFormSubmit = (e) => {
        e.preventDefault();
        if (this.state.firstname.length === 0) {
            // first timetrue
            this.setState({
                firstname: e.target.childNodes[0].childNodes[1].value
            })
        }

    }

    render() {
        const display = {
            display: this.props.display
        }
        console.log('firstname is ', this.state.firstname)
        return (
            <div style={display}>
                <form className="form" onSubmit={this.onFormSubmit} >
                    <label>
                        Firstname
                        <input type="text" />
                    </label>
                    <label>
                        lastname
                        <input type="text" />
                    </label>
                    <label>
                        Phone Number
                        <input type="tel" />
                    </label>
                    <input type="submit" value="Add contact" />
                </form>
            </div>
        );
    }
}
