import React from "react";
import ReactDOM from "react-dom";
import SearchField from "./SearchField";
import AddContact from "./AddContact";
import ContactForm from "./ContactForm";

import "./styles.css";

export default class ContactDiary extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      display: 'none',
      firstn
    }
  }

  onAddnewContact = (e) => {
    e.target.style.display = "none";
    this.setState({
      display: 'block'
    })
  }

  render() {
    return (
      <div className="App">
        <h1>React Contact Diary</h1>
        <SearchField />
        <AddContact onAddnewContact={this.onAddnewContact} />
        <ContactForm display={this.state.display} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<ContactDiary />, rootElement);
