import React from "react";
import ReactDOM from "react-dom";
import SearchField from "./SearchField";
import AddContact from "./AddContact";
import ContactForm from "./ContactForm";
import ContactList from "./ContactList";

import "./styles.css";

export default class ContactDiary extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      block: 'none',
      hide: 'none',
      master: []
    }
  }

  masterUpdate = (master) => {
    this.setState({
      master: master
    })
  }


  onAddnewContact = (e) => {
    // e.target.style.display = "none";
    // this.setState({
    //   block: 'block'
    // })
  }

  render() {
    return (
      <div className="App">
        <h1>React Contact Diary</h1>
        <SearchField />
        <AddContact onAddnewContact={this.onAddnewContact} hide={this.state.hide} />
        <ContactForm block={this.state.block} master={this.state.master}  />
        <br />
        <ContactList list={this.state.master} />
      </div>
    );
  }
}

const rootElement = document.getElementById("root");
ReactDOM.render(<ContactDiary />, rootElement);
