import React from "react";

export default class ContactForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: "",
            lastname: "",
            contact: "",

        }
    }

    onFormSubmit = (e) => {
        e.preventDefault();
        if (this.state.firstname.length === 0 || this.state.lastname.length === 0 || this.state.contact.length === 0) {
            alert('Please insert data on all fields!')
        }

        else {
            const master = [...this.props.master];
            const contact = {
                firstname: this.state.firstname,
                lastname: this.state.lastname,
                contact: this.state.contact
            }
            this.setState({
                thanks: 'block'
            })
            e.target.style.display = "none";

            master.push(contact)

            console.log(master)

        }
    }

    onFirstNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            firstname: e.target.value
        })
    }

    onLastNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            lastname: e.target.value
        })
    }

    onContactNumberInput = (e) => {
        console.log(e.target.value);
        this.setState({
            contact: e.target.value
        })
    }

    render() {
        const display = {
            display: this.props.block
        }
        const hide = {
            display: this.state.thanks
        }
        return (
            <div className="wrapper">
                <div style={display}>
                    <form className="form" onSubmit={this.onFormSubmit} >
                        <label>
                            Firstname
                        <input type="text" onChange={this.onFirstNameInput} />
                        </label>
                        <label>
                            lastname
                        <input type="text" onChange={this.onLastNameInput} />
                        </label>
                        <label>
                            Phone Number
                        <input type="tel" onChange={this.onContactNumberInput} />
                        </label>
                        <input type="submit" value="Add contact" />
                    </form>
                </div>
                {/* <div style={hide}>
                    <p>Thank you for adding contact!</p>
                </div> */}
            </div>
        );
    }
}
