import React from "react";


export default class ContactForm extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstname: "",
            lastname: "",
            contactno: ""
        }
    }

    onFormSubmit = (e) => {
        e.preventDefault();
        if (this.state.firstname.length === 0 || this.state.lastname.length === 0 || this.state.contact.length === 0) {
            alert('Please insert data on all fields!')
        }
        else {
            const contactInfo = {
                firstname: this.state.firstname,
                lastname: this.state.lastname,
                contactno: this.state.contactno
            }
            const contacts = [...this.props.contacts]
            contacts.push(contactInfo)
            this.setState({
                contacts: contacts
            })

            console.log(contacts)
        }
    }

    onFirstNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            firstname: e.target.value
        })
    }

    onLastNameInput = (e) => {
        console.log(e.target.value)
        this.setState({
            lastname: e.target.value
        })
    }

    onContactNumberInput = (e) => {
        console.log(e.target.value);
        this.setState({
            contact: e.target.value
        })
    }

    render() {
        return (
            <div className="wrapper">
                <div>
                    <form className="form" onSubmit={this.onFormSubmit} >
                        <label>
                            First name
                        <input type="text" onChange={this.onFirstNameInput} />
                        </label>
                        <label>
                            last name
                        <input type="text" onChange={this.onLastNameInput} />
                        </label>
                        <label>
                            Phone Number
                        <input type="tel" onChange={this.onContactNumberInput} />
                        </label>
                        <input type="submit" value="Add contact" />
                    </form>
                </div>
                {/* <div style={hide}>
                    <p>Thank you for adding contact!</p>
                </div> */}
            </div>
        );
    }
}
